import './index.css';
import './output.css';

function App() {
  return (
    <div className="bg-gray-800 flex flex-row h-screen justify-evenly">
      <div className="h-5/6 w-1/4 flex flex-col mx-16 my-16 p-8 rounded-2xl items-center bg-gray-800">
        <CardRowContainer text="Spirituality" color='bg-blue-300'>{CardRow()}</CardRowContainer>
        <CardRowContainer text="Islam" color='bg-green-300'>{CardRow()}</CardRowContainer>
        <CardRowContainer text="Philosophy" color='bg-red-300'>{CardRow()}</CardRowContainer>
        </div>
      <div className="h-5/6 w-1/3 flex flex-col mx-8 my-16 p-8 rounded-2xl shadow-lg bg-gray-600">
        <h1 className="text-white font-extrabold text-2xl mx-auto">Hello, world!</h1>
        <p className="text-white">Welcome to your new Tailwind CSS + React project!</p>
      </div>
      <div className="h-5/6 w-1/4 flex flex-col mx-16 my-16 p-8 rounded-2xl shadow-lg items-center bg-gray-600">
        </div>
    </div>
  );
}

function Card({children}){
  return (
    <div className="h-5/6 w-1/8 p-8 shadow-lg bg-gray-400 hover:bg-gray-300">
      {children}
      </div>
  );
}

function CardRow(){
  return (
    <div className="h-7/8 w-3/4 flex flex-row overflow-x-scroll gap-2 items-start">
      <Card />
      <Card />
      <Card />
      <Card />
      <Card />
      <Card />
      <Card />
      <Card />
      <Card />
      <Card />
      <Card />
      <Card />
      <Card />
      <Card />
      <Card />
      </div>
  );
}

function CardRowContainer({text, children, color}){
  const containerCSS = ['h-1/8', 'w-full', 'flex', 'flex-col', 'mx-4', 'my-4', 'p-2', 'shadow-lg', 'items-start', 'rounded-2xl', color]
  const containerCSSFINAL = containerCSS.join(' ')
  return (
    <div className={containerCSSFINAL}>
      <h1 className="text-white font-extrabold text-2xl justify-center">{text}</h1>
      <div className="h-1/8 w-3/4 flex flex-col mx-4 my-4 p-2 items-start ">
      {children}
      </div>
      </div>
  );
}

export default App;
