import '../Styling/index.css';
import '../Styling/output.css';

import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';

/*
Summary: This file defines the template components that will be movable
         The templates represent the possible components that the user can place in the container components
*/

/**
 * Template: This template represents a template that will be placed in a container
 * @param {id} id: The id of the template
 * @param {children} children: The children of the template
 */
function Template({id, children}){

    let styles;

    if (id === 'Directory'){
        styles = 'h-full w-full flex flex-col p-8 rounded-2xl items-center bg-gray-800';
    } else if (id === 'Note'){
        styles = 'h-full w-full flex flex-col p-8 rounded-2xl shadow-lg bg-gray-600';
    } else if (id === 'Online'){
        styles = 'h-full w-full flex flex-col p-8 rounded-2xl shadow-lg bg-gray-600';
    }

    return (
      <div className={styles}>
        {children}
        </div>
    );
}

export {Template};
