import './Styling/index.css';
import './Styling/output.css';

import {
  DndContext,
  closestCenter
} from "@dnd-kit/core";
import {
  arrayMove,
  horizontalListSortingStrategy,
  rectSwappingStrategy,
  SortableContext,
  verticalListSortingStrategy,
} from "@dnd-kit/sortable";

import {useState} from 'react';

import {Card, CardRow, CardRowContainer} from './CardDir/CardSection'
import {NoteTemplate, DirectoryTemplate, OnlineTemplate} from './Containers&Templates/Templates'
import {Container} from './Containers&Templates/Containers'

let strat = rectSwappingStrategy

function App() {

  const [containers, setContainers] = useState(["Auxilary", "Primary", "Tetriary"]);
  const [templateType, setTemplates] = useState(["1", "2", "3"])

  //fix this so that I can drag and drop the templates into different containers
  return (
    <DndContext
        collisionDetection={closestCenter}
        onDragEnd={handleDragEnd}>
      <div className="bg-gray-800 flex flex-row h-screen justify-evenly">
      <SortableContext
              items={templateType}
              strategy={strat}>
        <Container dropId = {0} id={containers[0]}>

              <DirectoryTemplate id={templateType[0]}>
                <CardRowContainer text="Spirituality" color='bg-blue-300'>{CardRow()}</CardRowContainer>
                <CardRowContainer text="Islam" color='bg-green-300'>{CardRow()}</CardRowContainer>
                <CardRowContainer text="Philosophy" color='bg-red-300'>{CardRow()}</CardRowContainer>
              </DirectoryTemplate>

        </Container>
        </SortableContext>

        <SortableContext
              items={templateType}
              strategy={strat}>
        <Container dropId = {1} id={containers[1]}>

                <NoteTemplate id={templateType[1]}>
                <h1 className="text-white font-extrabold text-2xl mx-auto">Hello, world!</h1>
                <p className="text-white">Welcome to your new Tailwind CSS + React project!</p>
                </NoteTemplate>

        </Container>
        </SortableContext>

        <SortableContext
              items={templateType}
              strategy={strat}>
        <Container dropId = {2} id={containers[2]}>

              <OnlineTemplate id={templateType[2]}>
              </OnlineTemplate>

        </Container>
        </SortableContext>

      </div>
    </DndContext>
  );

  function handleDragEnd(event) {
    const {over} = event;

    // If the item is dropped over a container, set it as the parent
    // otherwise reset the parent to `null`
    setParent(over ? over.id : null);
  }
}

export default App;
